// Product functions
export const getAdminProducts = (filters = {}) => {
  let filteredProducts = [...products];
  console.log(filters);

  // Apply filters
  if (filters.search) {
    const searchTerm = filters?.search?.toLowerCase();
    filteredProducts = filteredProducts.filter(
      (product) =>
        product?.name?.toLowerCase().includes(searchTerm) ||
        product?.sku?.toLowerCase().includes(searchTerm)
    );
  }

  if (filters.category) {
    filteredProducts = filteredProducts.filter(
      (product) => product.category === filters.category
    );
  }

  if (filters.stock === "inStock") {
    filteredProducts = filteredProducts.filter((product) => product.stock > 0);
  } else if (filters.stock === "outOfStock") {
    filteredProducts = filteredProducts.filter(
      (product) => product.stock === 0
    );
  }

  if (filters.featured === true) {
    filteredProducts = filteredProducts.filter((product) => product.featured);
  }

  // Sort
  if (filters.sort) {
    switch (filters.sort) {
      case "name_asc":
        filteredProducts.sort((a, b) => a.name.localeCompare(b.name));
        break;
      case "name_desc":
        filteredProducts.sort((a, b) => b.name.localeCompare(a.name));
        break;
      case "price_asc":
        filteredProducts.sort((a, b) => a.price - b.price);
        break;
      case "price_desc":
        filteredProducts.sort((a, b) => b.price - a.price);
        break;
      case "stock_asc":
        filteredProducts.sort((a, b) => a.stock - b.stock);
        break;
      case "stock_desc":
        filteredProducts.sort((a, b) => b.stock - a.stock);
        break;
      case "date_asc":
        filteredProducts.sort(
          (a, b) => new Date(a.dateAdded) - new Date(b.dateAdded)
        );
        break;
      case "date_desc":
        filteredProducts.sort(
          (a, b) => new Date(b.dateAdded) - new Date(a.dateAdded)
        );
        break;
      default:
        break;
    }
  }

  return Promise.resolve({
    products: filteredProducts,
    total: filteredProducts.length,
  });
};

export const getAdminProductById = (id) => {
  const product = products.find((p) => p.id === parseFloat(id));
  return Promise.resolve(product || null);
};

export const createAdminProduct = (productData) => {
  const newProduct = {
    id: `product_${Date.now()}`,
    dateAdded: new Date().toISOString(),
    dateModified: new Date().toISOString(),
    ...productData,
  };

  products.unshift(newProduct);

  return Promise.resolve(newProduct);
};

export const updateAdminProduct = (id, productData) => {
  const index = products.findIndex((p) => p.id === parseFloat(id));

  if (index === -1) {
    return Promise.reject(new Error("Product not found"));
  }

  const updatedProduct = {
    ...products[index],
    ...productData,
    dateModified: new Date().toISOString(),
  };

  products[index] = updatedProduct;

  return Promise.resolve(updatedProduct);
};

export const deleteAdminProduct = (id) => {
  const index = products.findIndex((p) => p.id === id);

  if (index === -1) {
    return Promise.reject(new Error("Product not found"));
  }

  const deletedProduct = products[index];
  products.splice(index, 1);

  return Promise.resolve(deletedProduct);
};

