// Customer functions
export const getAdminCustomers = (filters = {}) => {
  let filteredCustomers = [...customers];

  // Apply filters
  if (filters.search) {
    const searchTerm = filters.search.toLowerCase();
    filteredCustomers = filteredCustomers.filter(
      (customer) =>
        `${customer.firstName} ${customer.lastName}`
          .toLowerCase()
          .includes(searchTerm) ||
        customer.email.toLowerCase().includes(searchTerm)
    );
  }

  if (filters.status) {
    filteredCustomers = filteredCustomers.filter(
      (customer) => customer.status === filters.status
    );
  }

  // Sort
  if (filters.sort) {
    switch (filters.sort) {
      case "name_asc":
        filteredCustomers.sort((a, b) =>
          `${a.firstName} ${a.lastName}`.localeCompare(
            `${b.firstName} ${b.lastName}`
          )
        );
        break;
      case "name_desc":
        filteredCustomers.sort((a, b) =>
          `${b.firstName} ${b.lastName}`.localeCompare(
            `${a.firstName} ${a.lastName}`
          )
        );
        break;
      case "email_asc":
        filteredCustomers.sort((a, b) => a.email.localeCompare(b.email));
        break;
      case "email_desc":
        filteredCustomers.sort((a, b) => b.email.localeCompare(a.email));
        break;
      case "orders_asc":
        filteredCustomers.sort((a, b) => a.totalOrders - b.totalOrders);
        break;
      case "orders_desc":
        filteredCustomers.sort((a, b) => b.totalOrders - a.totalOrders);
        break;
      case "spent_asc":
        filteredCustomers.sort((a, b) => a.totalSpent - b.totalSpent);
        break;
      case "spent_desc":
        filteredCustomers.sort((a, b) => b.totalSpent - a.totalSpent);
        break;
      case "date_asc":
        filteredCustomers.sort(
          (a, b) => new Date(a.dateRegistered) - new Date(b.dateRegistered)
        );
        break;
      case "date_desc":
        filteredCustomers.sort(
          (a, b) => new Date(b.dateRegistered) - new Date(a.dateRegistered)
        );
        break;
      default:
        break;
    }
  }

  return Promise.resolve({
    customers: filteredCustomers,
    total: filteredCustomers.length,
  });
};

