// Recent orders for dashboard
export const getAdminRecentOrders = (limit = 5) => {
  const recentOrders = [...orders]
    .sort((a, b) => new Date(b.date) - new Date(a.date))
    .slice(0, limit);

  return Promise.resolve(recentOrders);
};

// Top selling products
export const getAdminTopSellingProducts = (limit = 5) => {
  // Calculate product sales from orders
  const productSales = {};

  orders.forEach((order) => {
    order.items.forEach((item) => {
      if (!productSales[item.id]) {
        productSales[item.id] = {
          id: item.id,
          name: item.name,
          totalSold: 0,
          totalRevenue: 0,
        };
      }

      productSales[item.id].totalSold += item.quantity;
      productSales[item.id].totalRevenue += item.price * item.quantity;
    });
  });

  const topProducts = Object.values(productSales)
    .sort((a, b) => b.totalSold - a.totalSold)
    .slice(0, limit);

  return Promise.resolve(topProducts);
};

// Revenue by date range
export const getAdminRevenueByDateRange = (dateFrom, dateTo) => {
  const startDate = new Date(dateFrom);
  const endDate = new Date(dateTo);
  endDate.setHours(23, 59, 59, 999); // End of day

  // Filter orders by date range
  const filteredOrders = orders.filter((order) => {
    const orderDate = new Date(order.date);
    return orderDate >= startDate && orderDate <= endDate;
  });

  // Group by date
  const revenueByDate = {};

  filteredOrders.forEach((order) => {
    const date = new Date(order.date).toISOString().split("T")[0]; // YYYY-MM-DD

    if (!revenueByDate[date]) {
      revenueByDate[date] = 0;
    }

    revenueByDate[date] += order.total;
  });

  // Convert to array of objects
  const result = Object.entries(revenueByDate).map(([date, revenue]) => ({
    date,
    revenue,
  }));

  // Sort by date
  result.sort((a, b) => new Date(a.date) - new Date(b.date));

  return Promise.resolve(result);
};

